<?php

namespace Pequiven\MasterBundle\Repository;

use Doctrine\ORM\EntityRepository;
use Pequiven\MasterBundle\Entity\Personal;

/**
 * PersonalRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PersonalRepository extends EntityRepository
{
    
    /**
     * 
     * @param type $num_personal: NÃºmero de Personal
     * @return type
     */
    public function getByNumPersonal($num_personal){
        $em = $this->getEntityManager();
        $query = $em->createQueryBuilder()
                    ->select('p')
                    ->from('\Pequiven\MasterBundle\Entity\Personal', 'p')
                    ->where('p.numPersonal = ' . $num_personal)
                    ->getQuery();
        return $query->getResult();
    }
    
}